import React, { useState } from "react";
import { Route, Switch } from "react-router-dom";
import Home from "./Components/Home";
import SketchPad from "./Components/SketchPad";
import Pictures from "./Components/Pictures";
import NavBar from "./Components/NavBar";
import Loading from "./Components/loading";
import { useAuth0 } from "@auth0/auth0-react";
import ProtectedRoute from "./auth/protected-route";

function App() {
  const [pictures, setPictures] = useState([]);
  const [feed, setFeed] = useState([]);
  const [user, setUser] = useState("hank95");

  const { isLoading } = useAuth0();
  if (isLoading) {
    return <Loading />;
  }

  useState(() => {
    fetch("http://localhost:3000/posts")
      .then((res) => res.json())
      .then((json) => {
        setFeed(json);
      });
  }, []);

  const handleSave = (e, title) => {
    if (
      e.target.previousSibling.toDataURL() ===
      "data:image/png;base64,"
    ) {
      return;
    }
    const pic = e.target.previousSibling.toDataURL();
    fetch("http://localhost:3000/posts", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        title: title,
        author: user,
        image: pic,
      }),
    })
      .then((res) => res.json())
      .then((json) => {
        setFeed([...feed, json]);
      });

    setPictures([...pictures, pic]);
  };

  const handleDelete = (id) => {
    const newFeed = feed.filter((item) => item.id !== id);
    setFeed(newFeed);
    fetch(`http://localhost:3000/posts/${id}`, {
      method: "DELETE",
    });
  };

  return (
    <div className="App">
      <NavBar />
      <Switch>
        <Route
          path="/sketch-pad"
          component={() => <SketchPad handleSave={handleSave} user={user} />}
        />
        <Route
          path="/my-sketchs"
          component={() => (
            <Pictures
              userInside={user}
              feed={feed}
              handleDelete={handleDelete}
            />
          )}
        />
        <Route
          exact
          path="/"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: "100vh",
          }}
          component={() => <Home feed={feed} />}
        />
      </Switch>
    </div>
  );
}

export default App;
