import React, { useEffect, useRef, useState } from "react";
import StyleBar from "./StyleBar";
const SketchPad = ({ handleSave }) => {
  const canvasRef = useRef(null);
  const contextRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [canvas, setCanvas] = useState(null);
  const [title, setTitle] = useState("");
  const [formData, setFormData] = useState({
    color: "",
    stroke: 3,
  });

  const handleData = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };
  const handleChange = (e) => {
    setTitle(e.target.value);
  };

  let pathsry = [];
  let points = [];
  let mouse = { x: 0, y: 0 };
  let previous = { x: 0, y: 0 };

  useEffect(() => {
    const newCanvas = canvasRef.current;
    newCanvas.width = 500;
    newCanvas.height = 500;
    newCanvas.style.width = `${500}px`;
    newCanvas.style.height = `${500}px`;
    newCanvas.style.border = "3px solid black";
    newCanvas.fillStyle = "white";
    setCanvas(newCanvas);
  }, []);
  useEffect(() => {
    if (canvas) {
      const context = canvas.getContext("2d");
      context.scale(1, 1);
      context.lineCap = "round";
      context.strokeStyle = formData.color;
      context.lineWidth = formData.stroke;
      context.fillStyle = "white";
      contextRef.current = context;
    }
  }, [canvas, formData.color, formData.stroke]);

  // a function to detect the mouse position
  function oMousePos(canvas, evt) {
    var ClientRect = canvas.getBoundingClientRect();
    return {
      //objeto
      x: Math.round(evt.clientX - ClientRect.left),
      y: Math.round(evt.clientY - ClientRect.top),
    };
  }

  const startDrawing = ({ e }) => {
    previous = { x: mouse.x, y: mouse.y };
    mouse = oMousePos(canvas, e);
    points = [];
    points.push({ x: mouse.x, y: mouse.y });
    setIsDrawing(true);
  };
  const endDrawing = () => {
    pathsry.push(points);
    contextRef.current.closePath();
    setIsDrawing(false);
  };
  const draw = ({ e }) => {
    if (!isDrawing) {
      return;
    }
    // const { offsetX, offsetY } = nativeEvent;
    // contextRef.current.lineTo(offsetX, offsetY);
    // contextRef.current.stroke();
    previous = { x: mouse.x, y: mouse.y };
    mouse = oMousePos(canvas, e);
    // saving the points in the points array
    points.push({ x: mouse.x, y: mouse.y });
    // drawing a line from the previous point to the current point
    contextRef.current.beginPath();
    contextRef.current.moveTo(previous.x, previous.y);
    contextRef.current.lineTo(mouse.x, mouse.y);
    contextRef.current.stroke();
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.fillStyle = "white";
    context.fillRect(0, 0, canvas.width, canvas.height);
  };

  function drawPaths() {
    // delete everything
    contextRef.current.clearRect(0, 0, canvas.width, canvas.height);
    // draw all the paths in the paths array
    pathsry.forEach((path) => {
      contextRef.current.beginPath();
      contextRef.current.moveTo(path[0].x, path[0].y);
      for (let i = 1; i < path.length; i++) {
        contextRef.current.lineTo(path[i].x, path[i].y);
      }
      contextRef.current.stroke();
    });
  }

  function Undo() {
    // remove the last path from the paths array
    pathsry.splice(-1, 1);
    // draw all the paths in the paths array
    drawPaths();
  }

  return (
    <div className="sketch">
      <StyleBar handleData={handleData} formData={formData} />
      <label className="title" for="title">
        Title:
      </label>
      <input
        className="inputField"
        type="text"
        name="title"
        onChange={handleChange}
        placeholder="Your Masterpiece"
      />
      <canvas
        className="canvas"
        onMouseDown={startDrawing}
        onMouseUp={endDrawing}
        onMouseMove={draw}
        ref={canvasRef}
      />
      <button className="saveBtn" onClick={(e) => handleSave(e, title)}>
        Save
      </button>
      <button className="clearBtn" onClick={clearCanvas}>
        Clear
      </button>
      <button className="undoBtn" onClick={undoCanvas}>
        Undo
      </button>
    </div>
  );
};

export default SketchPad;
