import React, { useState } from "react";
import { Route, Switch } from "react-router-dom";
import Home from "./Components/Home";
import SketchPad from "./Components/SketchPad";
import Pictures from "./Components/Pictures";
import NavBar from "./Components/NavBar";

function App() {
  const [pictures, setPictures] = useState([]);
  const [feed, setFeed] = useState([]);
  const [user, setUser] = useState("hank95");

  useState(() => {
    fetch("http://localhost:3000/posts")
      .then((res) => res.json())
      .then((json) => {
        setFeed(json);
      });
  }, []);

  const handleSave = (e, title) => {
    if (
      e.target.previousSibling.toDataURL() ===
      "data:image/png;base64,"
    ) {
      return;
    }
    const pic = e.target.previousSibling.toDataURL();
    fetch("http://localhost:3000/posts", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        title: title,
        author: user,
        image: pic,
      }),
    })
      .then((res) => res.json())
      .then((json) => {
        setFeed([...feed, json]);
      });

    setPictures([...pictures, pic]);
  };
  console.log(feed);
  console.log(pictures);
  return (
    <div className="App">
      <NavBar />
      <Switch>
        <Route
          path="/sketch-pad"
          component={() => <SketchPad handleSave={handleSave} user={user} />}
        />
        <Route
          path="/my-sketchs"
          component={() => <Pictures pictures={pictures} />}
        />
        <Route exact path="/" component={() => <Home feed={feed} />} />
      </Switch>
    </div>
  );
}

export default App;
