import React, { useState } from "react";
import { Route, Switch } from "react-router-dom";
import Axios from "axios";
import Home from "./Components/Home";
import SketchPad from "./Components/SketchPad";
import Pictures from "./Components/Pictures";
import NavBar from "./Components/NavBar";

function App() {
  const [feed, setFeed] = useState([]);
  const [user, setUser] = useState("");

  useState(() => {
    Axios.get("http://localhost:3001/api/get").then((response) => {
      setFeed(response.data);
    });
  }, []);

  // const { isLoading } = useAuth0();
  // if (isLoading) {
  //   return <Loading />;
  // }
  const handleSave = (e, title) => {
    if (
      e.target.parentElement.previousSibling.toDataURL() ===
      "data:image/png;base64,"
    ) {
      return;
    }
    const curDate = new Date().toLocaleDateString();
    const pic = e.target.parentElement.previousSibling.toDataURL();
    Axios.post("http://localhost:3001/api/insert", {
      title: title,
      author: user,
      image: pic,
      datePosted: curDate,
    }).then((res) => {
      console.log(res);
      Axios.get("http://localhost:3001/api/get").then((response) => {
        setFeed(response.data);
      });
    });
  };

  const handleDelete = (id) => {
    console.log(id);
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setFeed(
        feed.filter((val) => {
          return val.iduser_posts !== id;
        })
      );
    });
  };

  const nameGrabber = (nameInput) => {
    setUser(nameInput);
  };
  const [nameInput, setNameInput] = useState("");
  const handleUpdate = (e) => {
    setNameInput(e.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    nameGrabber(nameInput);
    console.log(nameInput);
  };

  if (!user) {
    return (
      <div>
        <h1>Who are you?</h1>
        <div>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="username"
              onChange={handleUpdate}
              placeholder="Username"
              value={nameInput}
            />
            <input type="submit" value="login" className="button3" />
          </form>
        </div>
      </div>
    );
  }
  return (
    <div className="App">
      <NavBar />
      <Switch>
        <Route
          path="/sketch-pad"
          component={() => <SketchPad handleSave={handleSave} user={user} />}
        />
        <Route
          path="/my-sketchs"
          component={() => (
            <Pictures
              userInside={user}
              feed={feed}
              handleDelete={handleDelete}
              nameGrabber={nameGrabber}
            />
          )}
        />
        <Route
          exact
          path="/"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: "100vh",
          }}
          component={() => <Home feed={feed} />}
        />
      </Switch>
    </div>
  );
}

export default App;
