import React, { useState } from "react";
import { Card, Image } from "semantic-ui-react";
import Heart from "react-animated-heart";

// import LikeButton from "./LikeButton";

const PictureCard = ({ post }) => {
  let clickcount = post.likeCount;
  const [isClick, setClick] = useState(false);
  const [count, setCount] = useState(clickcount);

  function handleClick() {
    if (isClick) return;
    setCount((previousCount) => previousCount + 1);

    // handleLikeUpdate(count, post.iduser_posts);
    setClick(!isClick);
  }
  const handleLikeUpdate = (count, id) => {
    Axios.put("https://pictogram-db.herokuapp.com/update", {
      likeCount: count,
      iduser_posts: id,
    }).then((response) => {
      console.log(response);
      setFeed(
        feed.map((val) => {
          return val.id === id
            ? {
                iduser_posts: val.iduser_posts,
                title: val.title,
                author: val.author,
                image: val.image,
                likeCount: count + 1,
              }
            : val;
        })
      );
    });
  };
  console.log(post.iduser_posts);

  return (
    <Card className="Card">
      <Image src={post.image} wrapped ui={false} />
      <Card.Content className="CardContent">
        <Card.Header>{post.title}</Card.Header>
        <Card.Meta>{post.author}</Card.Meta>
        <Card.Meta>{post.datePosted}</Card.Meta>
        <div className="Appz">
          <p className="Counter">{count}</p>
          <Heart
            className="Heart"
            isClick={isClick}
            onClick={() => {
              console.log("button");
              handleClick();
              handleLikeUpdate(count, post.iduser_posts);
            }}
          />
        </div>
      </Card.Content>
    </Card>
  );
};

export default PictureCard;
