import React, { useState } from "react";
import { Card, Image } from "semantic-ui-react";
import Heart from "react-animated-heart";

// import LikeButton from "./LikeButton";

const PictureCard = (post, handleLikeUpdate) => {
  const [isClick, setClick] = useState(false);
  const [count, setCount] = useState(post.post.likeCount);

  function handleClick() {
    if (isClick) {
      setCount(count - 1);
    } else {
      setCount(count + 1);
    }
    setClick(!isClick);
    handleLikeUpdate(count, post.post.iduser_posts);
  }

  return (
    <Card className="Card">
      <Image src={post.post.image} wrapped ui={false} />
      <Card.Content className="CardContent">
        <Card.Header>{post.post.title}</Card.Header>
        <Card.Meta>{post.post.author}</Card.Meta>
        <Card.Meta>{post.post.datePosted}</Card.Meta>
        <div className="Appz">
          <p className="Counter">{count}</p>
          <Heart className="Heart" isClick={isClick} onClick={handleClick} />
        </div>
      </Card.Content>
    </Card>
  );
};

export default PictureCard;
