import PictureCard from "./PictureCard";
import { Button } from "semantic-ui-react";
import { useState } from "react";
const Pictures = ({ feed, userInside, handleDelete, user, nameGrabber }) => {
  const filteredPosts = feed.filter((post) => post.author === userInside);

  const [nameInput, setNameInput] = useState("");
  const handleUpdate = (e) => {
    setNameInput(e.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    nameGrabber(nameInput);
  };

  if (!user) {
    return (
      <div>
        <h1>Who are you?</h1>
        <div>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="username"
              onChange={handleUpdate}
              placeholder="Username"
              value={nameInput}
            />
            <input type="submit" value="login" className="button3" />
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="myPicList">
      <h1>My Art</h1>
      {filteredPosts.map((pic) => {
        console.log(pic);
        return (
          <div>
            <Button
              className="discard"
              onClick={
                pic.author === userInside
                  ? () => handleDelete(pic.iduser_posts)
                  : null
              }
            >
              Discard
            </Button>
            <PictureCard post={pic} />
          </div>
        );
      })}
    </div>
  );
};

export default Pictures;
