import React, { useState } from "react";
import { Route, Switch } from "react-router-dom";
import Axios from "axios";
import Home from "./Components/Home";
import SketchPad from "./Components/SketchPad";
import Pictures from "./Components/Pictures";
import NavBar from "./Components/NavBar";
import { Icon } from "semantic-ui-react";

function App() {
  const [feed, setFeed] = useState([]);
  const [user, setUser] = useState("");

  useState(() => {
    Axios.get("https://pictogram-db.herokuapp.com/api/get").then((response) => {
      setFeed(response.data);
    });
  }, []);

  const handleSave = (e, title) => {
    if (
      e.target.parentElement.previousSibling.toDataURL() ===
      "data:image/png;base64,"
    ) {
      return;
    }
    const curDate = new Date().toLocaleDateString();
    const pic = e.target.parentElement.previousSibling.toDataURL();
    Axios.post("https://pictogram-db.herokuapp.com/api/insert", {
      title: title,
      author: user,
      image: pic,
      datePosted: curDate,
    }).then((res) => {
      console.log(res);
      Axios.get("https://pictogram-db.herokuapp.com/api/get").then(
        (response) => {
          setFeed(response.data);
        }
      );
    });
  };

  const handleDelete = (id) => {
    console.log(id);
    Axios.delete(`https://pictogram-db.herokuapp.com/delete/${id}`).then(
      (response) => {
        setFeed(
          feed.filter((val) => {
            return val.iduser_posts !== id;
          })
        );
      }
    );
  };
  // const handleLikeUpdate = (count, id) => {
  //   Axios.put("https://pictogram-db.herokuapp.com/update", {
  //     likeCount: count,
  //     iduser_posts: id,
  //   }).then((response) => {
  //     console.log(response);
  //     setFeed(
  //       feed.map((val) => {
  //         return val.id === id
  //           ? {
  //               iduser_posts: val.iduser_posts,
  //               title: val.title,
  //               author: val.author,
  //               image: val.image,
  //               likeCount: count + 1,
  //             }
  //           : val;
  //       })
  //     );
  //   });
  // };

  const nameGrabber = (nameInput) => {
    setUser(nameInput);
  };
  const [nameInput, setNameInput] = useState("");
  const handleUpdate = (e) => {
    setNameInput(e.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    nameGrabber(nameInput);
    console.log(nameInput);
  };

  if (!user) {
    return (
      <div className="feedList">
        <div>
          <Icon name="camera" size="large" />
        </div>
        <h1>Welcome to pictogram!</h1>
        <h2>What should we call you?</h2>
        <div>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="username"
              onChange={handleUpdate}
              placeholder="Username"
              value={nameInput}
            />
            <input type="submit" value="login" className="button3" />
          </form>
        </div>
      </div>
    );
  }
  return (
    <div className="App">
      <NavBar user={user} />
      <Switch>
        <Route
          path="/sketch-pad"
          component={() => <SketchPad handleSave={handleSave} user={user} />}
        />
        <Route
          path="/my-sketchs"
          component={() => (
            <Pictures
              userInside={user}
              feed={feed}
              handleDelete={handleDelete}
              nameGrabber={nameGrabber}
            />
          )}
        />
        <Route
          exact
          path="/"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: "100vh",
          }}
          component={() => <Home feed={feed} handleLikeUpdate={null} />}
        />
      </Switch>
    </div>
  );
}

export default App;
